import React from "react";
import A1 from "../img/A-layer1.svg";
import A2 from "../img/A-layer1.svg";
import A3 from "../img/A-layer1.svg";
import LayeredA from "./LayeredA";
import { Section, Description, Image, Hide } from "../styles";
//Framer Motion
import { motion } from "framer-motion";
import { titleAnim, fade, photoAnim } from "../animation";
import Wave from "./Wave";
import styled from "styled-components";
//React Router
import { Link } from "react-router-dom";
import { HashLink } from "react-router-hash-link";

const HomeSection = () => {
  return (
    <Section id="home">
      <Description>
        <motion.div>
          <Hide>
            <motion.h2 variants={titleAnim}>Hi,</motion.h2>
          </Hide>
          <Hide>
            <motion.h2 variants={titleAnim}>
              I'm <span>Alex</span>.
            </motion.h2>
          </Hide>
          <Hide>
            <motion.h2 variants={titleAnim}>Software Engineer</motion.h2>
          </Hide>
        </motion.div>
        <HashLink to="/#about">
          <HomePageButton variants={fade}>About Me</HomePageButton>
        </HashLink>
        <HashLink to="/#skills">
          <HomePageButton className="skills-button" variants={fade}>
            Skills
          </HomePageButton>
        </HashLink>
      </Description>
      <Wave />
    </Section>
  );
};

const HomePageButton = styled(motion.button)`
  .skills-button {
    margin: 1rem;
  }
`;

export default HomeSection;
