import React from "react";
import A from "../img/A.svg";
import home1 from "../img/home1.png";
import { About, Description, Image, Hide } from "../styles";
//Framer Motion
import { motion } from "framer-motion";
import { titleAnim, fade, photoAnim } from "../animation";
import Wave from "./Wave";
import styled from "styled-components";

const AboutSection = () => {
  return (
    <About>
      <Description>
        <motion.div>
          <Hide>
            <motion.h2 variants={titleAnim}>Hi,</motion.h2>
          </Hide>
          <Hide>
            <motion.h2 variants={titleAnim}>
              I'm <span>Alex</span>.
            </motion.h2>
          </Hide>
          <Hide>
            <motion.h2 variants={titleAnim}>Software Engineer</motion.h2>
          </Hide>
        </motion.div>
        <motion.p variants={fade}>Placeholder(delete)</motion.p>
        <motion.button variants={fade}>About Me</motion.button>
      </Description>
      <StyledA>
        <motion.img variants={photoAnim} src={A} alt="guy with a camera" />
      </StyledA>
      <Wave />
    </About>
  );
};

const StyledA = styled(Image)`
  img {
    height: 60vh;
    object-fit: fill;
    overflow: visible;
    position: relative;
    rotate: -15deg;
  }
`;

export default AboutSection;
