import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import {
  motion,
  useViewportScroll,
  useMotionValue,
  useTransform,
} from "framer-motion";

//Import Images
import A1 from "../img/A-layer1.svg";
import A2 from "../img/A-layer2.svg";
import A3 from "../img/A-layer3.svg";

//Import Animations
import { a2LayeredAnim } from "../animation";
import { a3LayeredAnim } from "../animation";

const LayeredA = () => {
  // Do calculations for scroll position
  const { scrollYProgress } = useViewportScroll();
  const scrollYPercentage = useTransform(
    scrollYProgress,
    (value) => value * -100
  );
  //A2 Layer Values
  const a2Position = useTransform(scrollYPercentage, (value) => value * 0.35);
  //A3 Layer Values
  const a3Position = useTransform(scrollYPercentage, (value) => value * 0.7);

  return (
    <CombinedA>
      <svg
        class="Alayer1"
        width="389.063"
        height="426.563"
        viewBox="0 0 389.063 426.563"
        xmlns="http://www.w3.org/2000/svg"
      >
        <g
          id="svgGroup"
          stroke-linecap="round"
          fill-rule="evenodd"
          font-size="9pt"
          stroke="#14ffec"
          stroke-width="0.25mm"
          fill="#14ffec"
          style="stroke:#14ffec;stroke-width:0.25mm;fill:#14ffec"
        >
          <path
            d="M 311.719 426.563 L 276.855 327.246 L 111.621 327.246 L 77.051 426.563 L 0 426.563 L 161.133 0 L 227.637 0 L 389.063 426.563 L 311.719 426.563 Z M 194.238 90.527 L 132.422 267.48 L 256.055 267.48 L 194.238 90.527 Z"
            vector-effect="non-scaling-stroke"
          />
        </g>
      </svg>
    </CombinedA>
  );
};

const CombinedA = styled(motion.div)`
  position: sticky;
  top: 25vh;
  background: none;
  z-index: 8;
`;

const StyledA1 = styled(motion.img)`
  z-index: 8;
  opacity: 100%;
  position: absolute;
  top: 5vh;
  right: 20vw;
  scale: 1.5;
  .Alayer1:hover {
    fill: #111;
  }
`;

const StyledA2 = styled(StyledA1)`
  z-index: 7;
  .Alayer2:hover {
    fill: #111;
  }
`;
const StyledA3 = styled(StyledA1)`
  z-index: 6;
  .Alayer3:hover {
    fill: #111;
  }
`;

export default LayeredA;
