import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import {
  motion,
  useViewportScroll,
  useMotionValue,
  useTransform,
} from "framer-motion";

//Import Images
import { ReascComponent as A1 } from "../img/A-layer1.svg";
import A2 from "../img/A-layer2.svg";
import A3 from "../img/A-layer3.svg";

//Import Animations
import { a2LayeredAnim } from "../animation";
import { a3LayeredAnim } from "../animation";

const LayeredA = () => {
  // Do calculations for scroll position
  const { scrollYProgress } = useViewportScroll();
  const scrollYPercentage = useTransform(
    scrollYProgress,
    (value) => value * -100
  );
  //A2 Layer Values
  const a2Position = useTransform(scrollYPercentage, (value) => value * 0.35);
  //A3 Layer Values
  const a3Position = useTransform(scrollYPercentage, (value) => value * 0.7);

  return (
    <CombinedA>
      <A1 />
      <StyledA1
        style={{
          scale: 1,
          rotate: "-15deg",
        }}
        src={A1}
        alt="First letter A layer"
      />
      <StyledA2
        style={{ scale: 1, rotate: "-15deg", x: a2Position, y: a2Position }}
        src={A2}
        alt="Second letter A layer"
      />
      <StyledA3
        style={{
          scale: 1,
          rotate: "-15deg",
          x: a3Position,
          y: a3Position,
        }}
        src={A3}
        alt="Third letter A layer"
      />
    </CombinedA>
  );
};

const CombinedA = styled(motion.div)`
  position: sticky;
  top: 25vh;
  background: none;
  z-index: 8;
`;

const StyledA1 = styled(motion.img)`
  z-index: 8;
  opacity: 100%;
  position: absolute;
  top: 5vh;
  right: 20vw;
  scale: 1.5;
`;

const StyledA2 = styled(StyledA1)`
  z-index: 7;
`;
const StyledA3 = styled(StyledA1)`
  z-index: 6;
`;

export default LayeredA;
