import React from "react";
//Import Icons
import cSharpIcon from "../img/skills-icons/c_sharp_icon.svg";
import javascriptIcon from "../img/skills-icons/javascript_icon.svg";
import reactIcon from "../img/skills-icons/react_icon.svg";
import pythonIcon from "../img/skills-icons/Icons.js";
//import cIcon from "../img/skills-icons/c_programming_language_icon.svg";
//Styles
import styled from "styled-components";
import { Section, Description, Image } from "../styles";
import { fade } from "../animation";
import { useScroll } from "./useScroll";

const SkillsSection = () => {
  const [element, controls] = useScroll();
  return (
    <SkillsSummary
      id="skills"
      variants={fade}
      animate={controls}
      initial="hidden"
      ref={element}
    >
      <Description>
        <h2>
          My <span>Skills</span>
        </h2>
        <div>
          <h3 className="skills-title">Languages</h3>
          <Skills>
            <Skill>
              <div className="icon">
                <img style={{ scale: "65%" }} src={javascriptIcon} alt="" />
                <h3>Python</h3>
              </div>
            </Skill>
            <Skill>
              <div className="icon">
                <img src={reactIcon} alt="" />
                <h3>C</h3>
              </div>
            </Skill>
          </Skills>
        </div>
        <div>
          <h3 className="skills-title">Technologies</h3>
          <Skills>
            <Skill>
              <div className="icon">
                <img style={{ scale: "65%" }} src={javascriptIcon} alt="" />
                <h3>Python</h3>
              </div>
            </Skill>
            <Skill>
              <div className="icon">
                <img src={reactIcon} alt="" />
                <h3>C</h3>
              </div>
            </Skill>
          </Skills>
        </div>
        <div>
          <h3 className="skills-title">Tools</h3>
          <Skills>
            <Skill>
              <div className="icon">
                <img style={{ scale: "65%" }} src={javascriptIcon} alt="" />
                <h3>JavaScript</h3>
              </div>
            </Skill>
            <Skill>
              <div className="icon">
                <img src={reactIcon} alt="" />
                <h3>ReactJS</h3>
              </div>
            </Skill>
            <Skill>
              <div className="icon">
                <img src={cSharpIcon} alt="" />
                <h3>C#</h3>
              </div>
            </Skill>
          </Skills>
        </div>
      </Description>
    </SkillsSummary>
  );
};

const SkillsSummary = styled(Section)`
  width: 60vw;
  h2 {
    padding-bottom: 2rem;
  }
  p {
    width: 90%;
    padding: 1rem 0rem 2rem 0rem;
  }
`;
const Skills = styled.div`
  display: flex;
  flex-wrap: wrap;
  @media (max-width: 1300px) {
    justify-content: center;
  }
`;
const Skill = styled.div`
  flex-basis: 8rem;
  display: flex;
  align-items: center;
  justify-content: center;

  .icon {
    display: block;
    img {
    }
    h3 {
      background: none;
      color: white;
      padding: 1.5rem 0rem;
    }
  }
`;

export default SkillsSection;
