import React from "react";
//Import Icons
import cSharpIcon from "../img/skills-icons/c-sharp-icon.svg";
import javascriptIcon from "../img/skills-icons/javascript-icon.svg";
import reactIcon from "../img/skills-icons/react-icon.svg";
import home2 from "../img/home2.png";
//Styles
import styled from "styled-components";
import { About, Description, Image } from "../styles";
import { fade } from "../animation";
import { useScroll } from "./useScroll";

const AboutMeSection = () => {
  const [element, controls] = useScroll();
  return (
    <AboutMe variants={fade} animate={controls} initial="hidden" ref={element}>
      <Description>
        <h2>
          A bit <span>About Me</span>
        </h2>
        <p>
          I am an embedded software engineer that is learning Full Stack
          software development. The tech stack I am targeting is below.
        </p>
        <p>
          Apart from coding, I enjoy calisthenics, bouldering and learning
          foreign languages.
        </p>
        <h3 id="skills-title">Tech Stack</h3>
        <Skills>
          <Skill>
            <div className="icon">
              <img style={{ scale: "65%" }} src={javascriptIcon} alt="" />
              <h3>JavaScript</h3>
            </div>
          </Skill>
          <Skill>
            <div className="icon">
              <img src={reactIcon} alt="" />
              <h3>ReactJS</h3>
            </div>
          </Skill>
          <Skill>
            <div className="icon">
              <img src={cSharpIcon} alt="" />
              <h3>C#</h3>
            </div>
          </Skill>
        </Skills>
      </Description>
    </AboutMe>
  );
};

const AboutMe = styled(About)`
  width: 60vw;
  h2 {
    padding-bottom: 2rem;
  }
  p {
    width: 90%;
    padding: 1rem 0rem 2rem 0rem;
  }
`;
const Skills = styled.div`
  display: flex;
  flex-wrap: wrap;
  @media (max-width: 1300px) {
    justify-content: center;
  }
`;
const Skill = styled.div`
  flex-basis: 8rem;
  display: flex;
  align-items: center;
  justify-content: center;

  .icon {
    display: block;
    img {
    }
    h3 {
      background: none;
      color: white;
      padding: 1.5rem 0rem;
    }
  }
`;

export default AboutMeSection;
