import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import {
  motion,
  useViewportScroll,
  useMotionValue,
  useTransform,
} from "framer-motion";

//Import Images
import A1 from "../img/A-layer1.svg";
import A2 from "../img/A-layer2.svg";
import A3 from "../img/A-layer3.svg";

//Import Animations
import { a2LayeredAnim } from "../animation";
import { a3LayeredAnim } from "../animation";

const LayeredA = () => {
  // Do calculations for scroll position
  const { scrollYProgress } = useViewportScroll();
  const scrollYPercentage = useTransform(
    scrollYProgress,
    (value) => value * -100
  );
  //A2 Layer Values
  const a2Position = useTransform(scrollYPercentage, (value) => value * 0.35);
  //A3 Layer Values
  const a3Position = useTransform(scrollYPercentage, (value) => value * 0.7);

  return (
    <CombinedA>
      <StyledA1 src={A1} alt="First letter A layer" />
      <StyledA2
        style={{ x: a2Position, y: a2Position }}
        src={A2}
        alt="Second letter A layer"
      />
      <StyledA3
        style={{
          x: a3Position,
          y: a3Position,
        }}
        src={A3}
        alt="Third letter A layer"
      />
    </CombinedA>
  );
};

const CombinedA = styled(motion.div)`
  position: sticky;
  top: 25vh;
  background: none;
  z-index: 8;
`;

const StyledA1 = styled(motion.img)`
  rotate: -15deg;
  z-index: 8;
  opacity: 100%;
  position: absolute;
  top: 5vh;
  right: 20vw;
  scale: 1.5;
  //Use this later to make the A layers clickable for skills selection
  pointer-events: visiblePainted;
`;

const StyledA2 = styled(StyledA1)`
  z-index: 7;
`;
const StyledA3 = styled(StyledA1)`
  z-index: 6;
`;

export default LayeredA;
