import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { motion, useViewportScroll, useMotionValue } from "framer-motion";

//Import Images
import A1 from "../img/A-layer1.svg";
import A2 from "../img/A-layer2.svg";
import A3 from "../img/A-layer3.svg";

//Import Animations
import { a2LayeredAnim } from "../animation";
import { a3LayeredAnim } from "../animation";

const LayeredA = () => {
  const { scrollYProgress } = useViewportScroll();
  const scrollYPercentage = useMotionValue(scrollYProgress.number);

  return (
    <CombinedA>
      <StyledA1 src={A1} alt="First letter A layer" />
      <StyledA2
        style={{ scrollYProgress }}
        src={A2}
        alt="Second letter A layer"
      />
      <StyledA3
        style={{
          x: scrollYPercentage,
          y: scrollYProgress,
        }}
        src={A3}
        alt="Third letter A layer"
      />
    </CombinedA>
  );
};

const CombinedA = styled(motion.div)`
  position: sticky;
  top: 20vh;
  background: none;
  z-index: 8;
`;

const StyledA1 = styled(motion.img)`
  rotate: -15deg;
  z-index: 8;
  opacity: 100%;
  position: absolute;
  top: 5vh;
  right: 15vw;
  scale: 1.5;
`;

const StyledA2 = styled(StyledA1)`
  z-index: 7;
`;
const StyledA3 = styled(StyledA1)`
  z-index: 9;
`;

export default LayeredA;
